/* C program for Merge Sort */
//! CHANGE THIS TO FOR XY ARRAY
#include <stdio.h>
#include <stdlib.h>

// Merges two subarrays of arr[].
// First subarray is arr[l..m]
// Second subarray is arr[m+1..r]
void merge(int arr[], int l, int m, int r, int sortingVar)
{
	int i, j, k;
	int n1 = m - l + 1;
	int n2 = r - m;

	/* create temp arrays */
	int L[n1*2], R[n2*2];

	/* Copy data to temp arrays L[] and R[] */
	for (i = 0; i < n1; i++){
		L[i*2] = arr[(l + i)*2];
		L[i*2+1] = arr[(l + i)*2+1];
  }
	for (j = 0; j < n2; j++){
		R[j*2] = arr[(m + 1 + j)*2];
		R[j*2+1] = arr[(m + 1 + j)*2+1];
  }

	/* Merge the temp arrays back into arr[l..r]*/
	i = 0; // Initial index of first subarray
	j = 0; // Initial index of second subarray
	k = l; // Initial index of merged subarray
	while (i < n1 && j < n2) {
		if (L[i*2+sortingVar] < R[j*2+sortingVar]) {
			arr[k*2] = L[i*2];
			arr[k*2+1] = L[i*2+1];
			i++;
		} else if (L[i*2+sortingVar] == R[j*2+sortingVar]){
      if (L[i*2+1-sortingVar] <= R[j*2+1-sortingVar]){
        arr[k*2] = L[i*2];
        arr[k*2+1] = L[i*2+1];
        i++;
      } else{
        arr[k*2] = R[j*2];
        arr[k*2+1] = R[j*2+1];
        j++;
      }
		} else{
        arr[k*2] = R[j*2];
        arr[k*2+1] = R[j*2+1];
        j++;
    }
		k++;
	}

	/* Copy the remaining elements of L[], if there
	are any */
	while (i < n1) {
		arr[k*2] = L[i*2];
		arr[k*2+1] = L[i*2+1];
		i++;
		k++;
	}

	/* Copy the remaining elements of R[], if there
	are any */
	while (j < n2) {
		arr[k*2] = R[j*2];
		arr[k*2+1] = R[j*2+1];
		j++;
		k++;
	}
}

/* l is for left index and r is right index of the
sub-array of arr to be sorted */
void mergeSort(int arr[], int l, int r, int sortingVar)
{
	if (l < r) {
		// Same as (l+r)/2, but avoids overflow for
		// large l and h
		int m = l + (r - l) / 2;

		// Sort first and second halves
		mergeSort(arr, l, m, sortingVar);
		mergeSort(arr, m + 1, r, sortingVar);

		merge(arr, l, m, r, sortingVar);
	}
}

void printArray(int A[], int size)
{
	int i;
	for (i = 0; i < size; i++)
		printf("%d %d\n", A[i*2], A[i*2+1]);
	printf("\n");
}

